-include("test_constructs.hrl").

%-spec create_cars( basic_utils:count(), [ pid() ] ) -> [ pid() ].
%create_cars( CarCount, Routes ) ->
%	create_cars( CarCount, Routes, _Acc=[] ).
%

%create_cars( _CarCount=0, _Routes, Acc ) ->
%	Acc;

%
%create_cars( CarCount, Routes, Acc ) ->
%
%	CarPid = create_car( Routes, CarCount-1 ),
%
%	create_cars( CarCount-1, Routes, [ CarPid | Acc ] ).
%
%
%create_car( Routes, CarCount ) ->
%
%	CarName = io_lib:format( "car_~B",
%								  [ CarCount ] ),
%	
%
%	ElectedRouteIndex = class_RandomManager:get_uniform_value(
